package Conditional_StatementsAnd_Operators;

public class If_Else_If_block {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
			int a=10;
			int b=20;
			int c=30;
			
			if(a>b) 
			{
				System.out.println("1");
			}
			else if(b>c)
			{
				System.out.println("2");
			}
			else
			{
				System.out.println("3");
			}

		
		
		
	}

}
