package Conditional_StatementsAnd_Operators;

public class OR_NotOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age=18;
		int sal=20000;
		if(!(age>18 || sal>20000))
		{
			System.out.println("1");
			
		}
		else
		{
			System.out.println("2");
		}
		
	}

}
