package Conditional_StatementsAnd_Operators;

public class OR_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age=18;
		int sal=10000;
		if(age>18 || sal>10000)
		{
			System.out.println("1");
		
		}
		if(age>20 || sal>10000)
		{
			System.out.println("2");
		}
		else
		{
			System.out.println("3");
		}
		
		
	}

}
